    function hoverVideo(e) {  
        $('video', this).get(0).play(); 
    }

    function hideVideo(e) {
        $('video', this).get(0).pause(); 
    }
    
    $(window).scroll(function() {
        if ($(this).scrollTop() > 200) {
            $('#header').addClass('run');
        } else {
            $('#header').removeClass('run');
        }
    });
    
